import React, { useState } from "react";
import Header from "./Header";
import Body from "./Body";
import BackDrop from "../components/BackDrop";

const Main = () => {
  const { showCart, setShowCart } = useState(false);
  
  return (
    <section className="main">
      <Header />
      <Body />
      <BackDrop />
    </section>
  );
};

export default Main;
