import React from "react";
import CartItem from "./CartItem";
import { useContext } from "react";
import { itemContext } from "../store/ItemContest";

const Cart = (props) => {
  const { items, totalAmount } = useContext(itemContext);
  return (
    <section className="cart-box">
      <h2>Your Cart Items are here!</h2>
      {items.length < 1}
      {items.map((fruit) => (
        <CartItem fruit={fruit} key={fruit.id} />
      ))}
      <hr />
      <div className="total">
        <h1>Total Price</h1>
        <p>$ {totalAmount}</p>
      </div>
      <div className="btns">
        <button className="close-btn" onClick={props.hideCartHandler}>
          Close
        </button>
        <button className="order-btn">Go To Order</button>
      </div>
    </section>
  );
};

export default Cart;
