import React from "react";

const Card = ({ fruit }) => {
  return (
    <div className="card">
      <p className="card-title">{fruit.name}</p>
      <p className="card-description">{fruit.description}</p>
      <p className="card-">{fruit.price} Baht</p>
      <button>+ Add</button>
    </div>
  );
};

export default Card;
