import { createContext, useReducer, useState } from "react";

const initialState = {
  items: [],
  totalAmount: 0,
};

const itemReducer = (state, action) => {
  if (state.type === "Add_Item") {
    const updatedItem = state.items.concat(action.item);
    const updatedTotalAmount =
      state.totalAmount + action.item.price * action.item.amount;

    return {
      items: updatedItem,
      totalAmount: updatedTotalAmount,
    };
  }
  return initialState;
};

export const itemContext = createContext({
  items: [],
  totalAmount: 0,
  addItem: (item) => {},
  removeItem: (id) => {},
});

const ItemContextProvider = (props) => {
  use[(itemState, setItemState)] = useState();
  const addItemHandler = (item) => {
    dispatchItem({ type: "Add_Item", item });
  };
  const removeItemHandler = (item) => {
    dispatchItem({ type: "Remove_Item", item });
  };

  const itemCtx = {
    items: itemState.items,
    totalAmount: itemState.totalAmount,
    addItem: addItemHandler,
    removeItem: removeItemHandler,
  };

  const [itemState, dispatchItem] = useReducer(itemReducer, initialState);
  return (
    <itemContext.Provider value={itemCtx}>
      {props.children}
    </itemContext.Provider>
  );
};

export default ItemContextProvider;
