import React from "react";

const Card = ({ fruit }) => {
  return (
    <div className="card">
      <div>
        <p className="card-title">{fruit.name}</p>
        <p className="card-description">{fruit.description}</p>
      </div>
      
    </div>
  );
};

export default Card;
